import React from 'react';

import './sign-in.styles.scss';

class SignIn extends React.Component{

    constructor(props){
        super(props);
        this.state={
            email: '',
            password: ''
        }
    }

    handleSubmit=(event)=>{
        event.preventDefault();
        this.setState({email:'',password:''});
    }

    handleeChange=(event)=>{
        const { value, name} =event.target;
        this.setState({[name]:value});
    }

    render(){
        return(
            <div className='sign-in'>
                <h2>I already have an account</h2>
                <span>Sign in with your email and pss  </span>
                <form onSubmit={this.handleSubmit}>
                    <inpu name="email" onChange={this.handleChange} value={this.state.email} type="email" required />
                    <label>Email</label>
                    <inpu name="password" onChange={this.handleChange} value={this.state.password}  type="password" required />
                    <label>Password</label>

                    <input type="submit" value="Submit Form" />
                </form>
            </div>
        )
    }
}


export default SignIn;
