import React from 'react';

import './sign-in.styles.scss';
import FormInput from '../form-input/form-input.component';
class SignIn extends React.Component{

    constructor(props){
        super(props);
        this.state={
            email: '',
            password: ''
        }
    }

    handleSubmit=(event)=>{
        event.preventDefault();
        this.setState({email:'',password:''});
    }

    handleChange=(event)=>{
        const { value, name} =event.target;
        this.setState({[name]:value});
    }

    render(){
        return(
            <div className='sign-in'>
                <h2>I already have an account</h2>
                <span>Sign in with your email and pss  </span>
                <form onSubmit={this.handleSubmit}>
                    <FormInput name="email" handleChange={this.handleChange} value={this.state.email} label="email" type="email" required />
                    <FormInput name="password" handleChange={this.handleChange} value={this.state.password} label="password" type="password" required />

                    <input type="submit" value="Submit Form" />
                </form>
            </div>
        )
    }
}


export default SignIn;
